      0 0 60px rgba(255,215,0,0.4),
      inset 0 3px 0 rgba(255,255,255,0.6);
  }
}

@keyframes progress-glow {
  0% { 
    box-shadow: 
      0 0 20px rgba(255,215,0,0.6),
      0 4px 20px rgba(255,215,0,0.3);
  }
  100% { 
    box-shadow: 
      0 0 40px rgba(255,215,0,0.9),
      0 6px 30px rgba(255,215,0,0.5);
  }
}

.animate-float { animation: float 8s ease-in-out infinite; }
.animate-pulse-gold { animation: pulse-gold 3s ease-in-out infinite; }
.animate-slide-up { animation: slide-up 1s cubic-bezier(0.23, 1, 0.320, 1); }
.animate-fade-in { animation: fade-in 1.2s ease-out; }
.animate-scale-up { animation: scale-up 0.6s cubic-bezier(0.23, 1, 0.320, 1); }

/* Elite Floating Particles */
.floating-particles {
  position: absolute;
  width: 100%;
  height: 100%;
  overflow: hidden;
  pointer-events: none;
}

.particle {
  position: absolute;
  width: 6px;
  height: 6px;
  background: radial-gradient(circle, var(--executive-gold), transparent);
  border-radius: 50%;
  animation: float 12s ease-in-out infinite;
  opacity: 0.7;
  filter: blur(1px);
}

/* Elite Scrollbar */
::-webkit-scrollbar {
  width: 12px;
}

::-webkit-scrollbar-track {
  background: rgba(6, 9, 24, 0.8);
  border-radius: 6px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(135deg, var(--executive-gold), var(--executive-platinum));
  border-radius: 6px;
  border: 2px solid rgba(6, 9, 24, 0.8);
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(135deg, #FFED4A, var(--executive-gold));
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .container { padding: var(--spacing-md); }
  .section { margin: var(--spacing-2xl) 0; }
  .card { padding: var(--spacing-md); }
  .btn { padding: .6rem .8rem; font-size: 0.9rem; }
  h1 { font-size: clamp(1.5rem, 8vw, 2.5rem); }
  .text-gradient {
    font-size: clamp(2rem, 8vw, 4rem);
  }
  .glass-morphism {
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
  }
  .tech-card:hover {
    transform: translateY(-8px) scale(1.02);
  }
}

