    -webkit-backdrop-filter: blur(40px) saturate(180%);
    background: 
      linear-gradient(135deg, rgba(255,215,0,0.1) 0%, rgba(30,60,255,0.08) 50%, rgba(185,242,255,0.06) 100%);
    border: 1px solid rgba(255, 215, 0, 0.3);
    transition: all var(--transition-luxury);
    transform-style: preserve-3d;
  }

  .tech-card:hover {
    transform: translateY(-12px) rotateX(8deg) rotateY(8deg) scale(1.02);
    box-shadow: 
      0 32px 64px rgba(255, 215, 0, 0.4),
      0 0 100px rgba(255, 215, 0, 0.2),
      0 0 150px rgba(30, 60, 255, 0.1);
    border-color: rgba(255, 215, 0, 0.6);
  }

  /* Elite Text Gradients */
  .text-gradient {
    background: linear-gradient(135deg, var(--executive-gold) 0%, var(--executive-platinum) 50%, var(--executive-diamond) 100%);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    filter: drop-shadow(0 0 15px rgba(255,215,0,0.5));
  }

  /* Elite Animation Classes */
  .magnetic-hover {
    transition: all var(--transition-luxury);
  }

  .magnetic-hover:hover {
    transform: translateY(-10px) scale(1.05);
    filter: drop-shadow(0 20px 40px rgba(255,215,0,0.3));
  }

  .status-pulse {
    animation: elite-pulse 4s ease-in-out infinite;
  }

  .reveal-on-scroll {
    opacity: 0;
    transform: translateY(60px) rotateX(20deg);
    transition: all 1s cubic-bezier(0.23, 1, 0.320, 1);
  }

  .reveal-on-scroll.revealed {
    opacity: 1;
    transform: translateY(0) rotateX(0deg);
  }
}

/* Elite Keyframe Animations */
@keyframes float {
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  25% { transform: translateY(-15px) rotate(2deg); }
  75% { transform: translateY(15px) rotate(-2deg); }
}

@keyframes pulse-gold {
  0%, 100% { 
    box-shadow: 
      0 0 30px rgba(255,215,0,0.6),
      0 8px 32px rgba(255,215,0,0.3);
  }
  50% { 
    box-shadow: 
      0 0 50px rgba(255,215,0,0.9),
      0 12px 48px rgba(255,215,0,0.5);
  }
}

@keyframes slide-up {
  from { 
    opacity: 0; 
    transform: translateY(80px) scale(0.95); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0) scale(1); 
  }
}

@keyframes fade-in {
  from { opacity: 0; filter: blur(10px); }
  to { opacity: 1; filter: blur(0); }
}

@keyframes scale-up {
  from { transform: scale(0.8) rotateY(20deg); opacity: 0; }
  to { transform: scale(1) rotateY(0deg); opacity: 1; }
}

@keyframes holographic-shift {
  to { background-position: 200% center; }
}

@keyframes ai-pulse {
  0%, 100% { 
    opacity: 1; 
    transform: scale(1);
  }
  50% { 
    opacity: 0.9; 
    transform: scale(1.02);
  }
}

@keyframes elite-pulse {
  0%, 100% { 
    box-shadow: 
      0 8px 32px rgba(255,215,0,0.4), 
      0 0 40px rgba(255,215,0,0.2),
      inset 0 2px 0 rgba(255,255,255,0.4);
  }
  50% { 
    box-shadow: 
      0 12px 48px rgba(255,215,0,0.6), 
      0 0 60px rgba(255,215,0,0.4),
      inset 0 3px 0 rgba(255,255,255,0.6);
  }
}

@keyframes progress-glow {
  0% { 
    box-shadow: 
      0 0 20px rgba(255,215,0,0.6),
      0 4px 20px rgba(255,215,0,0.3);
  }
  100% { 
    box-shadow: 
      0 0 40px rgba(255,215,0,0.9),
      0 6px 30px rgba(255,215,0,0.5);
  }
}

.animate-float { animation: float 8s ease-in-out infinite; }
.animate-pulse-gold { animation: pulse-gold 3s ease-in-out infinite; }
.animate-slide-up { animation: slide-up 1s cubic-bezier(0.23, 1, 0.320, 1); }
.animate-fade-in { animation: fade-in 1.2s ease-out; }
.animate-scale-up { animation: scale-up 0.6s cubic-bezier(0.23, 1, 0.320, 1); }

/* Elite Floating Particles */
.floating-particles {
  position: absolute;
  width: 100%;
  height: 100%;
  overflow: hidden;
  pointer-events: none;
}

.particle {
  position: absolute;
  width: 6px;
  height: 6px;
  background: radial-gradient(circle, var(--executive-gold), transparent);
  border-radius: 50%;
  animation: float 12s ease-in-out infinite;
  opacity: 0.7;
  filter: blur(1px);
}

/* Elite Scrollbar */
::-webkit-scrollbar {
  width: 12px;
}

::-webkit-scrollbar-track {
  background: rgba(6, 9, 24, 0.8);
  border-radius: 6px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(135deg, var(--executive-gold), var(--executive-platinum));
  border-radius: 6px;
  border: 2px solid rgba(6, 9, 24, 0.8);
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(135deg, #FFED4A, var(--executive-gold));
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .container { padding: var(--spacing-md); }
  .section { margin: var(--spacing-2xl) 0; }
  .card { padding: var(--spacing-md); }
  .btn { padding: .6rem .8rem; font-size: 0.9rem; }
  h1 { font-size: clamp(1.5rem, 8vw, 2.5rem); }
  .text-gradient {
    font-size: clamp(2rem, 8vw, 4rem);
  }
  .glass-morphism {
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
  }
  .tech-card:hover {
    transform: translateY(-8px) scale(1.02);
  }
}

