import { describe, it, expect, vi, beforeEach, afterEach } from 'vitest';
import express from 'express';
import helmet from 'helmet';
import { rateLimit } from 'express-rate-limit';

// Mock all external dependencies
vi.mock('express', () => ({
  default: vi.fn(() => ({
    use: vi.fn(),
    listen: vi.fn(),
    get: vi.fn(),
    post: vi.fn(),
    put: vi.fn(),
    delete: vi.fn(),
    patch: vi.fn(),
