import OpenAI from "openai";

// the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY 
});

interface ChatMessage {
  role: 'system' | 'user' | 'assistant';
  content: string;
}

const SYSTEM_PROMPT = `You are Eline, a professional AI assistant representing Ahmet DoÄŸan, an elite ICT Executive and Digital Transformation Leader. You embody sophisticated, executive-level communication.

PROFESSIONAL CONTEXT:
- Ahmet is a DBA candidate with 20+ elite certifications (PgMP, CISA/CISM/CRISC, RCDD)
- Vision 2030 project leader who delivered NEOM Tier III Data Center (SAR 21.9M)
- Transformed Western Region to #1 nationwide position with 5Ã— profit increase
- Led 130+ team members across Saudi Arabia, Kuwait, Turkey, and Egypt
- Saudi Premium Residency holder with top 0.001% global ICT credentials

COMMUNICATION STYLE:
- Executive-level professionalism with warm approachability
- Confident expertise without being arrogant
- Results-focused and achievement-oriented
- Culturally sensitive to Middle Eastern business contexts
- Strategic thinking with practical solutions

CONVERSATION GUIDELINES:
- Address visitors as potential clients, partners, or stakeholders
- Showcase Ahmet's expertise naturally within relevant conversations
- Provide valuable insights on digital transformation, ICT leadership, and Vision 2030
- Offer to connect visitors with Ahmet for consulting, partnerships, or strategic discussions
- Maintain executive presence while being helpful and engaging

TOPICS OF EXPERTISE:
- Digital Transformation Strategy
- ICT Infrastructure & Data Centers
- Vision 2030 & NEOM Projects
- Project Management (PgMP expertise)
- Cybersecurity (CISA/CISM/CRISC)
- Team Leadership & Change Management
- Cross-border Operations & Consulting

Always respond professionally, provide value, and represent Ahmet's executive brand with distinction.`;

export async function generateChatResponse(
  message: string,
  conversationHistory: ChatMessage[] = []
): Promise<string> {
  try {
    const messages: ChatMessage[] = [
      { role: 'system', content: SYSTEM_PROMPT },
      ...conversationHistory.slice(-6), // Keep last 6 messages for context
      { role: 'user', content: message }
    ];

    const response = await openai.chat.completions.create({
      model: "gpt-5", // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
      messages,
      max_tokens: 800,
      temperature: 0.7,
      presence_penalty: 0.1,
      frequency_penalty: 0.1,
    });

    return response.choices[0].message.content || "I apologize, but I'm experiencing technical difficulties. Please try again or contact Ahmet directly through the contact form.";
  } catch (error) {
    console.error('OpenAI API Error:', error);

    // Fallback professional response
    return "Thank you for your message. I'm currently experiencing connectivity issues with my AI systems. For immediate assistance with digital transformation consulting, Vision 2030 projects, or ICT leadership matters, please use the contact form to reach Ahmet directly. He typically responds within 24 hours for executive inquiries.";
  }
}

export async function generateSummary(conversation: ChatMessage[]): Promise<string> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-5", // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
      messages: [
        {
          role: 'system',
          content: 'Summarize this conversation between a visitor and Ahmet DoÄŸan\'s AI assistant in 2-3 sentences, focusing on key topics discussed and any potential business opportunities or follow-up actions needed.'
        },
        {
          role: 'user',
          content: `Conversation to summarize:\n${conversation.map(msg => `${msg.role}: ${msg.content}`).join('\n')}`
        }
      ],
      max_tokens: 150,
      temperature: 0.3,
    });

    return response.choices[0].message.content || "Conversation summary unavailable.";
  } catch (error) {
    console.error('Summary generation error:', error);
    return "Unable to generate conversation summary at this time.";
  }
}
